// 快速迭代 dispatch_apply
dispatch_queue_t global = dispatch_queue_create("test", DISPATCH_QUEUE_CONCURRENT);
dispatch_apply(100, global, ^(size_t iteration) {
    sleep(1);
    NSLog(@"%lu",iteration);
});
